import numpy as np

def solve():
    """
    Computes the global maximum stable time step for several test cases
    of an explicit upwind finite volume scheme.
    """
    test_cases = [
        # Case A: Balanced heterogeneity
        {
            "cells": [
                {"id": 0, "area": 2.0, "porosity": 0.3, "faces": [
                    {"length": 1.0, "normal_vel": +0.5, "neighbor": 1},
                    {"length": 0.8, "normal_vel": -0.1, "neighbor": 2},
                    {"length": 1.2, "normal_vel": +0.2, "neighbor": None}
                ]},
                {"id": 1, "area": 2.5, "porosity": 0.3, "faces": [
                    {"length": 1.0, "normal_vel": -0.5, "neighbor": 0},
                    {"length": 0.9, "normal_vel": +0.3, "neighbor": 2},
                    {"length": 1.1, "normal_vel": +0.1, "neighbor": None}
                ]},
                {"id": 2, "area": 1.8, "porosity": 0.3, "faces": [
                    {"length": 0.8, "normal_vel": +0.1, "neighbor": 0},
                    {"length": 0.9, "normal_vel": -0.3, "neighbor": 1},
                    {"length": 1.0, "normal_vel": +0.05, "neighbor": None}
                ]}
            ]
        },
        # Case B: Small cell with strong outflow
        {
            "cells": [
                {"id": 0, "area": 0.2, "porosity": 0.25, "faces": [
                    {"length": 0.5, "normal_vel": +3.0, "neighbor": 1},
                    {"length": 0.4, "normal_vel": +1.0, "neighbor": None},
                    {"length": 0.3, "normal_vel": +0.5, "neighbor": None}
                ]},
                 {"id": 1, "area": 5.0, "porosity": 0.25, "faces": [
                    {"length": 0.5, "normal_vel": -3.0, "neighbor": 0},
                    {"length": 1.2, "normal_vel": +0.2, "neighbor": None},
                    {"length": 1.0, "normal_vel": -0.1, "neighbor": None}
                ]}
            ]
        },
        # Case C: Heterogeneous porosity dominating
        {
            "cells": [
                {"id": 0, "area": 1.0, "porosity": 0.1, "faces": [
                    {"length": 0.6, "normal_vel": +0.8, "neighbor": 1},
                    {"length": 0.5, "normal_vel": +0.4, "neighbor": None},
                    {"length": 0.5, "normal_vel": -0.2, "neighbor": None}
                ]},
                {"id": 1, "area": 1.0, "porosity": 0.7, "faces": [
                    {"length": 0.6, "normal_vel": -0.8, "neighbor": 0},
                    {"length": 0.6, "normal_vel": +0.8, "neighbor": 2},
                    {"length": 0.5, "normal_vel": +0.1, "neighbor": None}
                ]},
                {"id": 2, "area": 1.0, "porosity": 0.3, "faces": [
                    {"length": 0.6, "normal_vel": -0.8, "neighbor": 1},
                    {"length": 0.7, "normal_vel": +0.2, "neighbor": None}
                ]}
            ]
        },
        # Case D: No outflow
        {
            "cells": [
                {"id": 0, "area": 3.0, "porosity": 0.2, "faces": [
                    {"length": 1.0, "normal_vel": -0.5, "neighbor": None},
                    {"length": 2.0, "normal_vel": -0.1, "neighbor": None}
                ]}
            ]
        }
    ]

    results = []

    for case_data in test_cases:
        local_max_dts = []
        for cell in case_data["cells"]:
            phi_i = cell["porosity"]
            area_i = cell["area"]

            # Sum of |f| * max(a_if, 0) over all faces of the cell
            sum_of_outflows = 0.0
            for face in cell["faces"]:
                length_f = face["length"]
                a_if = face["normal_vel"]
                if a_if > 0:
                    sum_of_outflows += length_f * a_if

            # Calculate local max stable time step based on the derived formula
            if sum_of_outflows > 0:
                dt_i_max = (phi_i * area_i) / sum_of_outflows
            else:
                # No outflow, so the local time step is unconstrained.
                # Use the specified sentinel value.
                dt_i_max = 1e12

            local_max_dts.append(dt_i_max)

        # The global time step is the minimum of all local time steps
        global_max_dt = min(local_max_dts)
        results.append(global_max_dt)

    # Format the final output as specified
    formatted_results = [f"{res:.6f}" for res in results]
    print(f"[{','.join(formatted_results)}]")

solve()
