import numpy as np
from math import erf, sqrt

# Constants in atomic units (Hartree for energy, Bohr for length)
t0 = 0.5        # Hartree
alpha_t = 0.6   # Bohr^{-1}
U_inf = 1.5     # Hartree
kappa = 0.4     # Hartree
lam = 1.2       # Bohr^{-1}
c_pbe = 0.6     # dimensionless scaling for semilocal correlation proxy

def t_of_R(R):
    """Hopping integral t(R) in Hartree."""
    return t0 * np.exp(-alpha_t * R)

def U_of_R(R):
    """On-site repulsion U(R) in Hartree."""
    return U_inf - kappa * np.exp(-lam * R)

def E_exact(R):
    """Exact singlet ground-state energy of the half-filled Hubbard dimer in Hartree."""
    t = t_of_R(R)
    U = U_of_R(R)
    return 0.5 * U - sqrt((0.5 * U) ** 2 + 4.0 * t ** 2)

def E_RHF(R):
    """Restricted mean-field (symmetric single-determinant) energy in Hartree."""
    t = t_of_R(R)
    U = U_of_R(R)
    return -2.0 * t + 0.5 * U

def E_corr_strong(R):
    """Strong-coupling second-order correlation energy in Hartree: -4 t^2 / U."""
    t = t_of_R(R)
    U = U_of_R(R)
    return -4.0 * t ** 2 / U

def E_UHF(R):
    """Spin-symmetry-broken mean-field proxy energy in Hartree: -2 t^2 / U."""
    t = t_of_R(R)
    U = U_of_R(R)
    return -2.0 * t ** 2 / U

def E_PBE(R):
    """Semilocal PBE proxy energy in Hartree."""
    return E_RHF(R) + c_pbe * E_corr_strong(R)

def E_PBE0(R, alpha):
    """Global hybrid PBE0 proxy energy in Hartree with mixing fraction alpha."""
    return (1.0 - alpha) * E_PBE(R) + alpha * E_UHF(R)

def alpha_lr(R, omega):
    """Long-range mixing fraction alpha_lr(R; omega) = erf(omega R)."""
    return erf(omega * R)

def E_LC_omega_PBE(R, omega):
    """Range-separated hybrid LC-omega-PBE proxy energy in Hartree."""
    a_lr = alpha_lr(R, omega)
    return (1.0 - a_lr) * E_PBE(R) + a_lr * E_UHF(R)

def rmse_over_Rs(method_func, Rs, *params):
    """Compute RMSE in Hartree for a given method over separations Rs."""
    errors_sq = []
    for R in Rs:
        if params:
            Em = method_func(R, *params)
        else:
            Em = method_func(R)
        Ee = E_exact(R)
        errors_sq.append((Em - Ee) ** 2)
    return sqrt(np.mean(errors_sq))

def solve():
    # Separation set in Bohr
    Rs = [1.0, 1.4, 2.0, 3.0, 5.0, 8.0]

    # Scenario definitions: (alpha for PBE0, omega for LC-omega-PBE)
    scenarios = [
        ("A", 0.25, 0.5),  # happy path
        ("B", 0.0, 0.0),   # boundary semilocal limit
        ("C", 1.0, 2.0),   # boundary long-range/exact-exchange-like limit
    ]

    results = []
    for _, alpha, omega in scenarios:
        # PBE RMSE (does not depend on alpha or omega)
        rmse_pbe = rmse_over_Rs(E_PBE, Rs)
        # PBE0 RMSE with given alpha
        rmse_pbe0 = rmse_over_Rs(E_PBE0, Rs, alpha)
        # LC-omega-PBE RMSE with given omega
        rmse_lc = rmse_over_Rs(E_LC_omega_PBE, Rs, omega)
        results.extend([rmse_pbe, rmse_pbe0, rmse_lc])

    # Final print statement in the exact required format: nine floats, six decimals, in Hartree.
    print(f"[{','.join(f'{v:.6f}' for v in results)}]")

solve()
