import numpy as np
from scipy.linalg import expm, eigvals

def operator_matrix(bA, dA, bB, dB, sAB, sBA):
    """
    Construct the 2x2 birth-death-switchover operator matrix M.
    M = [[bA - dA - sAB, sBA],
         [sAB,           bB - dB - sBA]]
    """
    return np.array([[bA - dA - sAB, sBA],
                     [sAB,           bB - dB - sBA]], dtype=float)

def leading_exponent_constant(M):
    """
    Compute the leading Lyapunov exponent (largest real part of eigenvalues) for constant M.
    """
    vals = eigvals(M)
    # Largest real part
    return float(np.max(np.real(vals)))

def leading_exponent_periodic(M1, tau1, M2, tau2):
    """
    Compute the leading Lyapunov exponent for a periodic environment alternating M1 (duration tau1)
    and M2 (duration tau2). One-cycle propagator P = exp(M2*tau2) @ exp(M1*tau1).
    Lyapunov exponent lambda* = (1/(tau1+tau2)) * log(spectral_radius(P)).
    """
    P = expm(M2 * tau2) @ expm(M1 * tau1)
    # Spectral radius: max absolute value of eigenvalues
    evals = eigvals(P)
    rho = np.max(np.abs(evals))
    # Guard against numerical issues: rho must be positive real
    return float(np.log(rho) / (tau1 + tau2))

def solve():
    # Define the test cases from the problem statement.
    test_cases = [
        # Case 1: general constant environment
        {
            "type": "constant",
            "params": {
                "bA": 1.2, "dA": 0.3, "bB": 0.6, "dB": 0.7, "sAB": 0.15, "sBA": 0.05
            }
        },
        # Case 2: boundary case (no switching)
        {
            "type": "constant",
            "params": {
                "bA": 0.8, "dA": 0.1, "bB": 1.0, "dB": 1.2, "sAB": 0.0, "sBA": 0.0
            }
        },
        # Case 3: symmetric net growth and symmetric switching
        {
            "type": "constant",
            "params": {
                "bA": 0.8, "dA": 0.5, "bB": 0.75, "dB": 0.45, "sAB": 0.2, "sBA": 0.2
            }
        },
        # Case 4: periodic environment alternating between M1 and M2
        {
            "type": "periodic",
            "params": {
                # Environment 1
                "bA1": 1.2, "dA1": 0.3, "bB1": 0.5, "dB1": 0.7, "sAB1": 0.12, "sBA1": 0.04, "tau1": 2.5,
                # Environment 2
                "bA2": 0.7, "dA2": 0.9, "bB2": 1.4, "dB2": 0.5, "sAB2": 0.06, "sBA2": 0.18, "tau2": 1.5
            }
        }
    ]

    results = []
    for case in test_cases:
        ctype = case["type"]
        p = case["params"]
        if ctype == "constant":
            M = operator_matrix(p["bA"], p["dA"], p["bB"], p["dB"], p["sAB"], p["sBA"])
            lam = leading_exponent_constant(M)
            results.append(lam)
        elif ctype == "periodic":
            M1 = operator_matrix(p["bA1"], p["dA1"], p["bB1"], p["dB1"], p["sAB1"], p["sBA1"])
            M2 = operator_matrix(p["bA2"], p["dA2"], p["bB2"], p["dB2"], p["sAB2"], p["sBA2"])
            lam = leading_exponent_periodic(M1, p["tau1"], M2, p["tau2"])
            results.append(lam)
        else:
            # Should not occur given the test suite; default to NaN
            results.append(float("nan"))

    # Round to six decimal places and format as required.
    formatted = [f"{x:.6f}" for x in results]
    print(f"[{','.join(formatted)}]")

solve()
