import numpy as np

def format_result(val):
    """Formats a floating-point result to 3 decimal places or 'inf'."""
    if val == float('inf'):
        return 'inf'
    return f"{val:.3f}"

def solve():
    """
    Computes the expected time to emergence of an established resistant lineage
    for a series of test cases based on a stochastic birth-death model.
    """
    test_cases = [
        # Case 1 (monotherapy A, finite-time “happy path”)
        {
            "type": "mono",
            "params": {"N_S": 1e6, "b_S": 0.7, "mu_X": 1e-7, "b_RX": 0.8, "d_RX": 0.6}
        },
        # Case 2 (combination therapy with orthogonal AMPs, finite-time “happy path”)
        {
            "type": "combo",
            "params": {"N_S": 1e6, "b_S": 0.7, "mu_A": 1e-7, "mu_B": 2e-7,
                       "b_A": 0.3, "d_A": 0.9, "b_B": 0.25, "d_B": 0.85,
                       "b_DR": 0.85, "d_DR": 0.65}
        },
        # Case 3 (monotherapy B, boundary case with non-establishing resistance)
        {
            "type": "mono",
            "params": {"N_S": 1e6, "b_S": 0.7, "mu_X": 2e-7, "b_RX": 0.5, "d_RX": 0.6}
        },
        # Case 4 (combination therapy, near-critical single-resistant lineages)
        {
            "type": "combo",
            "params": {"N_S": 1e6, "b_S": 0.7, "mu_A": 5e-8, "mu_B": 5e-8,
                       "b_A": 0.49, "d_A": 0.5, "b_B": 0.2, "d_B": 0.6,
                       "b_DR": 0.9, "d_DR": 0.7}
        },
        # Case 5 (monotherapy A, low-hazard regime)
        {
            "type": "mono",
            "params": {"N_S": 1e5, "b_S": 0.5, "mu_X": 1e-8, "b_RX": 0.9, "d_RX": 0.8}
        }
    ]

    results = []

    for case in test_cases:
        params = case["params"]
        hazard_rate = 0.0

        if case["type"] == "mono":
            N_S, b_S, mu_X = params["N_S"], params["b_S"], params["mu_X"]
            b_RX, d_RX = params["b_RX"], params["d_RX"]

            # The resistant lineage must be supercritical to establish.
            if b_RX  d_RX:
                # Probability of establishment for a single mutant lineage.
                # P_est = (b_RX - d_RX) / b_RX
                # Using max(0, ...) to be robust, although checked by if.
                p_est_rx = max(0, b_RX - d_RX) / b_RX

                # Rate of generation of resistant mutants.
                rate_gen_x = N_S * b_S * mu_X

                hazard_rate = rate_gen_x * p_est_rx
            else:
                hazard_rate = 0.0

        elif case["type"] == "combo":
            N_S, b_S, mu_A, mu_B = params["N_S"], params["b_S"], params["mu_A"], params["mu_B"]
            b_A, d_A = params["b_A"], params["d_A"]
            b_B, d_B = params["b_B"], params["d_B"]
            b_DR, d_DR = params["b_DR"], params["d_DR"]

            # Double-resistant lineage must be supercritical to establish.
            if b_DR  d_DR:
                p_est_dr = (b_DR - d_DR) / b_DR

                # Contribution from S - A - DR pathway
                termA = 0.0
                if d_A  b_A and b_A  0: # Check for subcriticality and non-zero birth rate
                    termA = b_A / (d_A - b_A)

                # Contribution from S - B - DR pathway
                termB = 0.0
                if d_B  b_B and b_B  0: # Check for subcriticality and non-zero birth rate
                    termB = b_B / (d_B - b_B)

                if termA  0 or termB  0:
                    hazard_rate = (N_S * b_S * mu_A * mu_B) * (termA + termB) * p_est_dr
                else:
                    hazard_rate = 0.0
            else:
                hazard_rate = 0.0

        if hazard_rate  0:
            expected_time = 1.0 / hazard_rate
        else:
            expected_time = float('inf')

        results.append(expected_time)

    # Format the final output string exactly as required.
    print(f"[{','.join(map(format_result, results))}]")

solve()
