# The complete and runnable Python 3 code goes here.
# Imports must adhere to the specified execution environment.
import numpy as np

def solve():
    """
    Computes the surface temperature of a slab with temperature-dependent
    thermal conductivity using a Newton-Raphson solver.
    """

    def newton_solver(residual_func, derivative_func, T0, tol=1e-8, max_iter=50):
        """
        A robust Newton-Raphson solver for a scalar nonlinear equation.
        """
        Ts = float(T0)
        for _ in range(max_iter):
            res = residual_func(Ts)
            if abs(res)  tol:
                return Ts

            deriv = derivative_func(Ts)
            if abs(deriv)  1e-12:  # Safeguard against division by zero
                # This should not be reached for k(T) > 0.
                raise ValueError("Derivative is too small; Newton's method fails.")

            # Standard Newton-Raphson step
            step = -res / deriv

            # A simple step-size damper for robustness, although not strictly
            # necessary for these monotonic problems.
            max_step = 100.0 # Limit temperature change to 100K per step
            if abs(step) > max_step:
                step = np.sign(step) * max_step

            Ts += step

        # If loop finishes without convergence, raise an error.
        raise RuntimeError(f"Newton's method did not converge after {max_iter} iterations.")

    # --- Test Case Definitions ---

    # Case A: Linear conductivity
    # k(T) = k0 * (1 + alpha * (T - T_ref))
    params_A = {'k0': 200.0, 'alpha': 1e-3, 'T_ref': 300.0, 
                'L': 0.01, 'q0': 5e5, 'Tb': 300.0}

    def k_A(T, p):
        return p['k0'] * (1.0 + p['alpha'] * (T - p['T_ref']))

    def integral_k_A(Ts, p):
        # Antiderivative of k0*(1-alpha*T_ref) + k0*alpha*T is
        # k0*(1-alpha*T_ref)*T + 0.5*k0*alpha*T^2
        def primitive(T_val):
            return p['k0'] * ((1.0 - p['alpha'] * p['T_ref']) * T_val + \
                              0.5 * p['alpha'] * T_val**2)
        return primitive(Ts) - primitive(p['Tb'])

    # Case B: Exponential conductivity
    # k(T) = k0 * exp(beta * (T - T_ref))
    params_B = {'k0': 150.0, 'beta': 5e-3, 'T_ref': 350.0,
                'L': 0.02, 'q0': 1e6, 'Tb': 350.0}

    def k_B(T, p):
        return p['k0'] * np.exp(p['beta'] * (T - p['T_ref']))

    def integral_k_B(Ts, p):
        # Antiderivative of k0 * exp(beta*(T-T_ref)) is (k0/beta) * exp(beta*(T-T_ref))
        def primitive(T_val):
            return (p['k0'] / p['beta']) * np.exp(p['beta'] * (T_val - p['T_ref']))
        return primitive(Ts) - primitive(p['Tb'])

    # Case C: Quadratic conductivity
    # k(T) = k0 * (1 + a1*(T-T_ref) + a2*(T-T_ref)^2)
    params_C = {'k0': 100.0, 'a1': 2e-3, 'a2': 1e-6, 'T_ref': 300.0,
                'L': 0.005, 'q0': -2e5, 'Tb': 400.0}

    def k_C(T, p):
        dT = T - p['T_ref']
        return p['k0'] * (1.0 + p['a1'] * dT + p['a2'] * dT**2)

    def integral_k_C(Ts, p):
        # Antiderivative of k0*(1+a1*u+a2*u^2) where u=T-T_ref is
        # k0*(u + a1/2*u^2 + a2/3*u^3)
        def primitive(T_val):
            dT = T_val - p['T_ref']
            return p['k0'] * (dT + (p['a1'] / 2.0) * dT**2 + (p['a2'] / 3.0) * dT**3)
        return primitive(Ts) - primitive(p['Tb'])

    # Case D: Piecewise-linear conductivity
    params_D = {'L': 0.03, 'q0': 3e5, 'Tb': 450.0, 'T_split': 600.0}

    def k_D(T, p):
        if T = p['T_split']:
            return 50.0 + 0.1 * (T - 300.0) # 20 + 0.1*T
        else:
            return 80.0 + 0.05 * (T - 600.0) # 50 + 0.05*T

    def integral_k_D(Ts, p):
        T_split = p['T_split']

        def primitive_1(T): return 20.0 * T + 0.05 * T**2
        def primitive_2(T): return 50.0 * T + 0.025 * T**2

        # Integral from a common reference point (arbitrarily 0K) to T
        def integral_from_ref(T):
            if T = T_split:
                return primitive_1(T)
            else:
                # Value at split + integral over the second part
                val_at_split = primitive_1(T_split)
                integral_part2 = primitive_2(T) - primitive_2(T_split)
                return val_at_split + integral_part2

        return integral_from_ref(Ts) - integral_from_ref(p['Tb'])


    # Case E: Zero-flux check
    params_E = {'k0': 75.0, 'beta': 2e-3, 'L': 0.01, 'q0': 0.0, 'Tb': 500.0}

    def k_E(T, p):
        return p['k0'] * np.exp(p['beta'] * T)

    def integral_k_E(Ts, p):
        def primitive(T_val):
            return (p['k0'] / p['beta']) * np.exp(p['beta'] * T_val)
        return primitive(Ts) - primitive(p['Tb'])

    test_cases = [
        (params_A, k_A, integral_k_A),
        (params_B, k_B, integral_k_B),
        (params_C, k_C, integral_k_C),
        (params_D, k_D, integral_k_D),
        (params_E, k_E, integral_k_E),
    ]

    results = []
    for params, k_func, integral_k_func in test_cases:
        q0 = params['q0']
        L = params['L']
        Tb = params['Tb']

        # Define residual and its derivative for the solver
        residual = lambda Ts: integral_k_func(Ts, params) - q0 * L
        derivative = lambda Ts: k_func(Ts, params)

        # Calculate a physically-based initial guess
        k_at_Tb = k_func(Tb, params)
        if abs(k_at_Tb)  1e-12: # Avoid division by zero in guess
            T0 = Tb 
        else:
            # T_s approx T_b + (q0 * L) / k(T_b)
            T0 = Tb + (q0 * L) / k_at_Tb

        # In zero-flux case, initial guess is the exact solution
        if q0 == 0.0:
            T_final = Tb
        else:
            T_final = newton_solver(residual, derivative, T0)

        # Append result rounded to 6 decimal places
        results.append(f"{T_final:.6f}")

    # Final print statement in the exact required format.
    print(f"[{','.join(results)}]")

solve()
