import numpy as np

def solve():
    """
    Solves the vacancy number density ODE for given parameters and times.
    """
    # Define the test cases from the problem statement.
    test_cases = [
        {
            "params": {
                "Gv": 1.0e23, "Kvi": 5.0e-17, "ni": 1.0e19,
                "kv2": 2.0e14, "Dv": 1.0e-18, "nv0": 0.0
            },
            "times": [0.0, 1.0e-4, 1.0e-3, 1.0e-2, 1.0e-1, 1.0]
        },
        {
            "params": {
                "Gv": 0.0, "Kvi": 5.0e-17, "ni": 1.0e19,
                "kv2": 2.0e14, "Dv": 1.0e-18, "nv0": 1.0e21
            },
            "times": [0.0, 1.0e-2, 1.0e-1, 1.0, 1.0e1]
        },
        {
            "params": {
                "Gv": 1.0e21, "Kvi": 1.0e-18, "ni": 1.0e16,
                "kv2": 1.0e12, "Dv": 1.0e-20, "nv0": 0.0
            },
            "times": [0.0, 1.0e1, 1.0e2, 1.0e3, 1.0e4]
        },
        {
            "params": {
                "Gv": 5.0e23, "Kvi": 1.0e-21, "ni": 1.0e10,
                "kv2": 2.0e14, "Dv": 1.0e-18, "nv0": 0.0
            },
            "times": [0.0, 1.0, 1.0e1, 1.0e2, 1.0e3]
        },
        {
            "params": {
                "Gv": 5.0e17, "Kvi": 1.0e-20, "ni": 1.0e10,
                "kv2": 1.0e8, "Dv": 1.0e-22, "nv0": 1.0e18
            },
            "times": [0.0, 1.0e1, 1.0e2]
        }
    ]

    all_results = []
    for case in test_cases:
        params = case["params"]
        times = case["times"]

        Gv = params["Gv"]
        Kvi = params["Kvi"]
        ni = params["ni"]
        kv2 = params["kv2"]
        Dv = params["Dv"]
        nv0 = params["nv0"]

        case_results = []

        # Calculate the total rate constant for vacancy removal
        lambda_val = Kvi * ni + kv2 * Dv

        for t in times:
            if np.isclose(lambda_val, 0.0):
                # Linear accumulation if removal mechanisms are absent
                nv_t = Gv * t + nv0
            else:
                # Numerically stable solution for the ODE
                # nv(t) = (Gv/lambda) * (1 - exp(-lambda*t)) + nv0 * exp(-lambda*t)
                # Rewritten for numerical stability using np.expm1 for (exp(x)-1)

                lambda_t = lambda_val * t

                # First term: Gv * (1 - exp(-lambda*t)) / lambda
                # = Gv * (- (exp(-lambda*t) - 1)) / lambda
                # = Gv * (-expm1(-lambda*t)) / lambda
                term1 = Gv * (-np.expm1(-lambda_t)) / lambda_val

                # Second term: nv0 * exp(-lambda*t)
                term2 = nv0 * np.exp(-lambda_t)

                nv_t = term1 + term2

            case_results.append(nv_t)

        all_results.append(case_results)

    # Format the final output according to problem specifications.
    # No spaces, scientific notation with 6 significant digits.
    inner_strings = [
        f"[{','.join(['{:.6e}'.format(v) for v in res])}]"
        for res in all_results
    ]
    final_output = f"[{','.join(inner_strings)}]"

    # Final print statement in the exact required format.
    print(final_output)

solve()
