import numpy as np

# A strict Russian professor does not tolerate magic numbers. All constants are defined.
R_AIR = 287.05  # J/(kg K)
T_ABS_ZERO_C = -273.15  # Celsius

class FluidProperties:
    """Base class for fluid properties."""
    def rho(self, T_k): raise NotImplementedError
    def mu(self, T_k): raise NotImplementedError
    def cp(self, T_k): raise NotImplementedError
    def k(self, T_k): raise NotImplementedError
    def enthalpy_integral(self, T1_k, T2_k): raise NotImplementedError

class WaterProperties(FluidProperties):
    """Properties for liquid water."""
    def _T_to_TC(self, T_k):
        return T_k + T_ABS_ZERO_C

    def rho(self, T_k):
        T_c = self._T_to_TC(T_k)
        return 1000.0 - 0.3 * (T_c - 4.0)

    def mu(self, T_k):
        return 2.414e-5 * 10**(247.8 / (T_k - 140.0))

    def cp(self, T_k):
        T_c = self._T_to_TC(T_k)
        t_prime = T_c - 20.0
        return 4179.0 + 0.1 * t_prime - 0.001 * t_prime**2

    def k(self, T_k):
        T_c = self._T_to_TC(T_k)
        return 0.561 + 0.0019 * T_c - 3.0e-6 * T_c**2

    def enthalpy_integral(self, T1_k, T2_k):
        # H(T) = integral of cp(T) dT from T1 to T2
        # Let t = T - 293.15 (since T_C - 20 = T - 273.15 - 20)
        # cp(t) = 4179.0 + 0.1*t - 0.001*t^2
        # integral(cp) dt = 4179.0*t + 0.05*t^2 - (0.001/3)*t^3
        t1 = T1_k - 293.15
        t2 = T2_k - 293.15

        H2 = 4179.0 * t2 + 0.05 * t2**2 - (0.001 / 3.0) * t2**3
        H1 = 4179.0 * t1 + 0.05 * t1**2 - (0.001 / 3.0) * t1**3
        return H2 - H1

class AirProperties(FluidProperties):
    """Properties for dry air (ideal gas)."""
    def __init__(self, p_pa):
        self.p_pa = p_pa
        self.pr = 0.71
        self.mu0 = 1.716e-5
        self.T0 = 273.15
        self.S = 110.4

    def rho(self, T_k):
        return self.p_pa / (R_AIR * T_k)

    def mu(self, T_k):
        return self.mu0 * (T_k / self.T0)**1.5 * (self.T0 + self.S) / (T_k + self.S)

    def cp(self, T_k):
        return 1006.0 + 0.1 * (T_k - 300.0)

    def k(self, T_k):
        return self.cp(T_k) * self.mu(T_k) / self.pr

    def enthalpy_integral(self, T1_k, T2_k):
        # cp(T) = 1006 + 0.1*(T - 300) = 976 + 0.1*T
        # integral(cp) dT = 976*T + 0.05*T^2
        H2 = 976.0 * T2_k + 0.05 * T2_k**2
        H1 = 976.0 * T1_k + 0.05 * T1_k**2
        return H2 - H1

class OilProperties(FluidProperties):
    """Properties for light lubricating oil."""
    def _T_to_TC(self, T_k):
        return T_k + T_ABS_ZERO_C

    def rho(self, T_k):
        return 860.0

    def mu(self, T_k):
        T_c = self._T_to_TC(T_k)
        return 0.2 * np.exp(-0.035 * (T_c - 40.0))

    def cp(self, T_k):
        return 2000.0

    def k(self, T_k):
        return 0.13

    def enthalpy_integral(self, T1_k, T2_k):
        return self.cp(T1_k) * (T2_k - T1_k)

def get_fluid_properties(name, p_pa=None):
    """Factory function for fluid properties."""
    if name.lower() == 'water':
        return WaterProperties()
    elif name.lower() == 'air':
        if p_pa is None:
            raise ValueError("Pressure must be provided for air.")
        return AirProperties(p_pa)
    elif name.lower() == 'oil':
        return OilProperties()
    else:
        raise ValueError(f"Unknown fluid: {name}")

def calculate_outlet_conditions(case):
    """Main solver for a single test case."""
    D, L, mdot, Q, T_in, fluid_name, p_pa = case
    fluid = get_fluid_properties(fluid_name, p_pa)

    N = 400
    TOL = 1e-8
    MAX_ITER_NEWTON = 20
    MAX_ITER_WALL_T = 20

    delta_x = L / N
    delta_Q = Q / N
    q_prime_prime = Q / (np.pi * D * L)

    T_b = T_in
    nu_list = []

    for i in range(N):
        # --- Local Heat Transfer Calculation at segment inlet T_b ---
        mu_b = fluid.mu(T_b)
        cp_b = fluid.cp(T_b)
        k_b = fluid.k(T_b)

        Re = (4 * mdot) / (np.pi * D * mu_b)
        Pr = cp_b * mu_b / k_b

        # Petukhov friction factor
        f = (0.790 * np.log(Re) - 1.64)**-2 if Re >= 3000 else 0. # Added check for validity

        # Gnielinski correlation
        if Re > 2300 and 0.5 = Pr = 2000:
            numerator = (f / 8) * (Re - 1000) * Pr
            denominator = 1 + 12.7 * (f / 8)**0.5 * (Pr**(2/3) - 1)
            Nu_base = numerator / denominator
        else: # Fallback or error, for this problem Re is always high enough
            # Could use a laminar correlation, but problem specifies turbulent
            Nu_base = 3.66 

        # --- Inner iteration for wall temperature and viscosity correction ---
        Nu_curr = Nu_base
        for _ in range(MAX_ITER_WALL_T):
            h = Nu_curr * k_b / D
            T_w = T_b + q_prime_prime / h

            # Check if wall temperature is within property validity range
            # A more robust implementation would handle this, but for these cases it's fine.
            mu_w = fluid.mu(T_w)

            phi = (mu_b / mu_w)**0.11
            Nu_new = phi * Nu_base

            if abs(Nu_new - Nu_curr) / Nu_curr  TOL:
                break
            Nu_curr = Nu_new

        nu_list.append(Nu_curr)

        # --- Temperature Update to next segment ---
        energy_added_per_mass = delta_Q / mdot

        # For constant cp (Oil), direct update
        if isinstance(fluid, OilProperties):
            T_b += energy_added_per_mass / fluid.cp(T_b)
        else:
            # Newton-Raphson to find T_b_next
            T_b_next = T_b  # Initial guess
            for _ in range(MAX_ITER_NEWTON):
                H_integral = fluid.enthalpy_integral(T_b, T_b_next)
                g = H_integral - energy_added_per_mass
                if abs(g)  TOL * energy_added_per_mass:
                    break

                # Check for zero derivative
                cp_next = fluid.cp(T_b_next)
                if abs(cp_next)  1e-6:
                    T_b_next += 0.001 # Perturb if derivative is zero
                    continue

                T_b_next -= g / cp_next
            T_b = T_b_next

    T_out = T_b
    Nu_bar = np.mean(nu_list)

    return T_out, Nu_bar


def solve():
    test_cases = [
        # D(m), L(m), mdot(kg/s), Q(W), Tin(K), fluid, pressure(Pa)
        (0.02, 10.0, 0.20, 30000.0, 300.0, 'water', None),
        (0.05, 5.0, 0.10, 2000.0, 300.0, 'air', 101325.0),
        (0.02, 6.0, 3.00, 20000.0, 360.0, 'oil', None),
    ]

    results = []
    for case in test_cases:
        T_out, Nu_bar = calculate_outlet_conditions(case)
        # Format results as strings with three decimal places
        results.append(f"[{T_out:.3f},{Nu_bar:.3f}]")

    # Final print statement in the exact required format
    print(f"[{','.join(results)}]")

solve()
