import numpy as np
from scipy.special import k1

def solve():
    """
    Solves for the maximum principal strain (macular distortion) at the origin
    for a set of biomechanical test cases of tractional retinal detachment.
    """

    # Define the test cases from the problem statement.
    test_cases = [
        {
            "T": 0.3, "k": 0.09, "tractions": [
                {"P": 0.8, "x_vec": (1.5, 0.0), "e_vec": (-1.0, 0.0)},
                {"P": 0.6, "x_vec": (-1.2, 1.0), "e_vec": (0.995037, -0.0995037)},
                {"P": 0.7, "x_vec": (0.0, -2.0), "e_vec": (0.0, 1.0)}
            ]
        },
        {
            "T": 0.3, "k": 0.09, "tractions": [
                {"P": 1.0, "x_vec": (10.0, 0.0), "e_vec": (-1.0, 0.0)}
            ]
        },
        {
            "T": 0.3, "k": 0.09, "tractions": [
                {"P": 0.5, "x_vec": (0.25, 0.25), "e_vec": (-0.70710678, -0.70710678)},
                {"P": 0.4, "x_vec": (-0.4, -0.2), "e_vec": (0.89442719, 0.4472136)}
            ]
        },
        {
            "T": 0.3, "k": 0.09, "tractions": [
                {"P": 0.5, "x_vec": (1.0, 0.0), "e_vec": (-1.0, 0.0)},
                {"P": 0.5, "x_vec": (-1.0, 0.0), "e_vec": (1.0, 0.0)}
            ]
        },
        {
            "T": 0.3, "k": 0.9, "tractions": [
                {"P": 0.8, "x_vec": (1.5, 0.0), "e_vec": (-1.0, 0.0)},
                {"P": 0.6, "x_vec": (-1.2, 1.0), "e_vec": (0.995037, -0.0995037)},
                {"P": 0.7, "x_vec": (0.0, -2.0), "e_vec": (0.0, 1.0)}
            ]
        }
    ]

    results = []
    for case in test_cases:
        T = case["T"]
        k = case["k"]
        tractions = case["tractions"]

        kappa = np.sqrt(k / T)

        eps_xx, eps_yy, eps_xy = 0.0, 0.0, 0.0

        for traction in tractions:
            P_n = traction["P"]
            x_n_vec = np.array(traction["x_vec"])
            e_n_vec = np.array(traction["e_vec"])

            x_n, y_n = x_n_vec
            e_nx, e_ny = e_n_vec

            r_n = np.linalg.norm(x_n_vec)

            # If a traction is at the origin, its contribution is singular.
            # The problem context implies tractions are not at the evaluation point.
            if r_n == 0:
                continue

            k1_val = k1(kappa * r_n)

            # Common scalar coefficient for this traction's contribution to strain
            common_coeff = (kappa / (4 * np.pi * T)) * (P_n / r_n) * k1_val

            # Add this traction's contribution to the total strain components
            eps_xx += common_coeff * (2 * x_n * e_nx)
            eps_yy += common_coeff * (2 * y_n * e_ny)
            eps_xy += common_coeff * (x_n * e_ny + y_n * e_nx)

        # Calculate the maximum principal strain (largest eigenvalue)
        # lambda = ( (eps_xx + eps_yy)/2 ) +/- sqrt( ( (eps_xx - eps_yy)/2 )**2 + eps_xy**2 )
        term1 = (eps_xx + eps_yy) / 2
        term2 = np.sqrt(((eps_xx - eps_yy) / 2)**2 + eps_xy**2)

        max_principal_strain = term1 + term2
        results.append(max_principal_strain)

    # Format output as a comma-separated list in brackets, with 6 decimal places.
    print(f"[{','.join([f'{r:.6f}' for r in results])}]")

solve()
