import numpy as np
from scipy.optimize import curve_fit

def solve():
    """
    Fits a saturable dose-effect model (Emax model) to pharmacodynamic data
    to estimate genotype-specific Emax and EC50 parameters.
    """

    # Define the test cases from the problem statement.
    test_cases = [
        # Test Case 1
        {
            "Genotype A": {
                "Dose": np.array([0, 2, 2, 5, 5, 10, 10, 25, 25]),
                "Baseline HR": np.array([80, 82, 78, 81, 79, 83, 77, 84, 80]),
                "Post-dose HR": np.array([80, 73.729, 69.329, 65.8, 64.5, 63.0, 57.4, 58.7, 55.2])
            },
            "Genotype B": {
                "Dose": np.array([0, 2, 2, 5, 5, 10, 10, 25, 25]),
                "Baseline HR": np.array([78, 80, 82, 79, 83, 84, 76, 85, 81]),
                "Post-dose HR": np.array([78, 77.747, 79.447, 74.3, 77.9, 76.2, 68.0, 72.1, 69.0])
            }
        },
        # Test Case 2
        {
            "Genotype A": {
                "Dose": np.array([0, 20, 20, 40, 40, 80, 80]),
                "Baseline HR": np.array([88, 90, 86, 87, 89, 90, 85]),
                "Post-dose HR": np.array([88, 59.765, 55.465, 54.742, 55.942, 56.266, 51.366])
            },
            "Genotype B": {
                "Dose": np.array([0, 20, 20, 40, 40, 80, 80]),
                "Baseline HR": np.array([92, 91, 90, 88, 89, 90, 87]),
                "Post-dose HR": np.array([92, 65.9, 65.2, 58.633, 59.833, 58.482, 55.082])
            }
        },
        # Test Case 3
        {
            "Genotype A": {
                "Dose": np.array([0, 1, 1, 2, 2, 4, 4, 8, 8]),
                "Baseline HR": np.array([80, 82, 78, 81, 79, 83, 77, 84, 80]),
                "Post-dose HR": np.array([80, 81.61, 77.31, 80.038, 78.238, 81.048, 75.148, 80.752, 76.252])
            },
            "Genotype B": {
                "Dose": np.array([0, 1, 1, 2, 2, 4, 4, 8, 8]),
                "Baseline HR": np.array([81, 80, 82, 79, 83, 84, 76, 85, 81]),
                "Post-dose HR": np.array([81, 75.733, 77.833, 72.057, 75.657, 72.889, 65.189, 69.215, 65.715])
            }
        }
    ]

    def emax_model(dose, Emax, EC50):
        """Saturable Emax model for dose-response relationship."""
        # Add a small epsilon to EC50 + dose to avoid division by zero if dose=-EC50, though not expected here
        return (Emax * dose) / (EC50 + dose + 1e-9)

    all_results = []

    # Define parameter bounds as specified
    bounds = ([0, 1e-3], [100, 200])
    # Define a robust initial guess
    p0 = [20, 10]

    for case_data in test_cases:
        case_results = []
        genotypes = ["Genotype A", "Genotype B"]

        for genotype in genotypes:
            data = case_data[genotype]
            dose = data["Dose"]
            baseline_hr = data["Baseline HR"]
            post_dose_hr = data["Post-dose HR"]

            # Calculate the effect (change in heart rate)
            delta_hr = baseline_hr - post_dose_hr

            # Perform non-linear least squares fitting
            try:
                popt, _ = curve_fit(emax_model, dose, delta_hr, bounds=bounds, p0=p0, maxfev=5000)
                Emax_fit, EC50_fit = popt
            except RuntimeError:
                # Fallback in case of fitting failure, though not expected with this data
                Emax_fit, EC50_fit = np.nan, np.nan

            # Append rounded results to the list for the current case
            case_results.append(round(Emax_fit, 2))
            case_results.append(round(EC50_fit, 2))

        all_results.append(case_results)

    # Final print statement in the exact required format.
    print(all_results)

solve()
