import numpy as np
from scipy.optimize import root_scalar

# Define potential functions and their derivatives for each type.
def potential_funcs(case):
    ptype = case['type']
    if ptype == 'harmonic':
        kappa = case['kappa']
        def V(r):
            return 0.5 * kappa * r**2
        def dV(r):
            return kappa * r
        def ddV(r):
            return kappa
        return V, dV, ddV
    elif ptype == 'kepler_conf':
        alpha = case['alpha']
        beta = case['beta']
        def V(r):
            return -alpha / r + 0.5 * beta * r**2
        def dV(r):
            return alpha / r**2 + beta * r
        def ddV(r):
            return -2.0 * alpha / r**3 + beta
        return V, dV, ddV
    elif ptype == 'poly6':
        kappa = case['kappa']
        delta = case['delta']
        lam = case['lambda']
        # V(r) = 0.5 kappa r^2 - 0.25 delta r^4 + 0.1 lambda r^6
        def V(r):
            return 0.5 * kappa * r**2 - 0.25 * delta * r**4 + 0.1 * lam * r**6
        def dV(r):
            return kappa * r - delta * r**3 + 0.6 * lam * r**5
        def ddV(r):
            return kappa - 3.0 * delta * r**2 + 3.0 * lam * r**4
        return V, dV, ddV
    else:
        raise ValueError("Unknown potential type")

def find_stationary_radii(dV, mu, rmin=1e-6, rmax=100.0, ngrid=2000):
    # F(r) = dV(r) - mu^2 / r^3
    def F(r):
        return dV(r) - (mu**2) / (r**3)
    # Sample grid and detect sign changes
    # Use logarithmic spacing near small r and linear towards larger r
    # Construct a grid that is dense near small r
    small = np.logspace(np.log10(rmin), np.log10(1.0), num=ngrid//2, base=10.0)
    large = np.linspace(1.0, rmax, num=ngrid//2)
    grid = np.unique(np.concatenate([small, large]))
    vals = np.array([F(r) for r in grid])
    roots = []
    for i in range(len(grid)-1):
        a, b = grid[i], grid[i+1]
        fa, fb = vals[i], vals[i+1]
        if np.isnan(fa) or np.isnan(fb):
            continue
        # Check for sign change
        if fa == 0.0:
            root = a
        elif fb == 0.0:
            root = b
        elif fa * fb  0.0:
            # Brent's method
            try:
                res = root_scalar(F, bracket=(a, b), method='brentq')
                if res.converged and res.root > 0:
                    root = res.root
                else:
                    continue
            except ValueError:
                continue
        else:
            continue
        # Deduplicate roots
        if all(abs(root - r0) > 1e-6 for r0 in roots):
            roots.append(root)
    roots.sort()
    return roots

def classify_equilibria(case):
    V, dV, ddV = potential_funcs(case)
    mu = case['mu']
    radii = find_stationary_radii(dV, mu)
    eqs = []
    for r in radii:
        omega = mu / (r**2)
        # Second derivative of effective potential: ddV + 3 mu^2 / r^4
        G = ddV(r) + 3.0 * (mu**2) / (r**4)
        stable = bool(G > 0.0)
        eqs.append([float(r), float(omega), stable])
    return eqs

def solve():
    # Define the test cases from the problem statement.
    test_cases = [
        {'type': 'harmonic', 'kappa': 2.0, 'mu': 1.5},                        # Case 1
        {'type': 'kepler_conf', 'alpha': 1.0, 'beta': 0.0, 'mu': 0.5},        # Case 2
        {'type': 'kepler_conf', 'alpha': 2.0, 'beta': 0.5, 'mu': 0.2},        # Case 3
        {'type': 'poly6', 'kappa': 1.0, 'delta': 3.0, 'lambda': 0.5, 'mu': 1e-6},  # Case 4
        {'type': 'harmonic', 'kappa': -1.0, 'mu': 0.2},                       # Case 5
    ]

    results = []
    for case in test_cases:
        eqs = classify_equilibria(case)
        results.append(eqs)

    # Final print statement in the exact required format.
    # Convert boolean to lowercase 'true'/'false' for standard JSON-like output
    def format_output(data):
        return '[' + ','.join(f'[{r}, {w}, {str(s).lower()}]' for r, w, s in data) + ']' if data else '[]'

    print(f"[{','.join(map(str, results))}]")

if __name__ == "__main__":
    solve()
