import numpy as np
from scipy import optimize

def solve():
    """
    Solves for the critical osteoclast lifespan leading to a saddle-node
    bifurcation and accelerated bone loss for a set of test cases.
    """

    test_cases = [
        # Case H (happy path)
        {
            "params": {
                "p_b0": 0.10, "p_bc": 1.80, "d_b": 0.60,
                "p_c0": 0.05, "p_cb": 2.50,
                "K_c": 0.80, "K_b": 0.80, "n": 4, "m": 4,
                "k_f": 0.40, "k_r": 0.70
            },
            "tau_min": 3.0, "tau_max": 15.0, "C_max": 30.0, "A": 0.20
        },
        # Case N (no tipping)
        {
            "params": {
                "p_b0": 0.50, "p_bc": 0.50, "d_b": 0.50,
                "p_c0": 0.50, "p_cb": 0.80,
                "K_c": 1.00, "K_b": 1.00, "n": 3, "m": 3,
                "k_f": 0.60, "k_r": 0.80
            },
            "tau_min": 3.0, "tau_max": 15.0, "C_max": 30.0, "A": 0.20
        },
        # Case B (boundary)
        {
            "params": {
                "p_b0": 0.20, "p_bc": 1.40, "d_b": 0.70,
                "p_c0": 0.08, "p_cb": 2.00,
                "K_c": 0.90, "K_b": 0.90, "n": 4, "m": 4,
                "k_f": 0.45, "k_r": 0.75
            },
            "tau_min": 2.0, "tau_max": 6.0, "C_max": 30.0, "A": 0.20
        }
    ]

    results = []

    # Define model functions
    def H_c(C, p):
        return C**p['n'] / (p['K_c']**p['n'] + C**p['n'])

    def H_b(B, p):
        return B**p['m'] / (p['K_b']**p['m'] + B**p['m'])

    def B_star(C, p):
        return (p['p_b0'] + p['p_bc'] * H_c(C, p)) / p['d_b']

    def f_C(C, tau_c, p):
        if C  0: return np.inf
        return p['p_c0'] + p['p_cb'] * H_b(B_star(C, p), p) - C / tau_c

    def dHc_dC(C, p):
        num = p['n'] * p['K_c']**p['n'] * C**(p['n'] - 1)
        den = (p['K_c']**p['n'] + C**p['n'])**2
        return num / den

    def dHb_dB(B, p):
        num = p['m'] * p['K_b']**p['m'] * B**(p['m'] - 1)
        den = (p['K_b']**p['m'] + B**p['m'])**2
        return num / den

    def dBstar_dC(C, p):
        return (p['p_bc'] / p['d_b']) * dHc_dC(C, p)

    def df_dC(C, tau_c, p):
        if C = 1e-9: # Avoid division by zero from C^(n-1) if n1
            return -1.0 / tau_c
        B = B_star(C, p)
        if B = 1e-9: # Avoid similar issues for B
            return -1.0 / tau_c
        return p['p_cb'] * dHb_dB(B, p) * dBstar_dC(C, p) - 1.0 / tau_c

    def dM_dt(C, p):
        return p['k_f'] * B_star(C, p) - p['k_r'] * C

    def find_equilibria(tau_c, p, C_max):
        roots = set()
        C_grid = np.linspace(1e-9, C_max, 1000)
        f_vals = [f_C(c, tau_c, p) for c in C_grid]
        for i in range(len(C_grid) - 1):
            if np.sign(f_vals[i]) != np.sign(f_vals[i+1]):
                try:
                    root = optimize.brentq(f_C, C_grid[i], C_grid[i+1], args=(tau_c, p))
                    roots.add(root)
                except (RuntimeError, ValueError):
                    continue
        return sorted(list(roots))

    def slope_at_low_eq(tau_c, p, C_max):
        equilibria = find_equilibria(tau_c, p, C_max)
        if not equilibria:
            return 1.0  # Convention: no low eq means we are past the bifurcation
        C_low = equilibria[0]
        return df_dC(C_low, tau_c, p)

    for case in test_cases:
        p = case["params"]
        tau_min, tau_max, C_max, A = case["tau_min"], case["tau_max"], case["C_max"], case["A"]

        # 1. Coarse scan for bifurcation bracket
        tau_grid = np.linspace(tau_min, tau_max, 100)
        last_slope = None
        tau_bracket = None

        for i, tau_c_val in enumerate(tau_grid):
            equilibria = find_equilibria(tau_c_val, p, C_max)
            if not equilibria:
                current_slope = 1.0 # Past bifurcation
            else:
                C_low = equilibria[0]
                current_slope = df_dC(C_low, tau_c_val, p)

            if last_slope is not None and last_slope  0 and current_slope = 0:
                tau_bracket = (tau_grid[i-1], tau_c_val)
                break
            last_slope = current_slope

        if tau_bracket is None:
            results.append(-1.0)
            continue

        # 2. Refine critical lifespan
        try:
            tau_c_critical = optimize.brentq(slope_at_low_eq, tau_bracket[0], tau_bracket[1], args=(p, C_max))
        except ValueError:
            results.append(-1.0)
            continue

        # 3. Post-bifurcation verification
        tau_post = tau_c_critical * (1.0 + 1e-6)
        if tau_post  tau_max:
             results.append(-1.0)
             continue

        equilibria_post = find_equilibria(tau_post, p, C_max)
        stable_equilibria_post = [c for c in equilibria_post if df_dC(c, tau_post, p)  0]

        if not stable_equilibria_post:
            results.append(-1.0)
            continue

        # System jumps to the highest stable equilibrium
        C_post = max(stable_equilibria_post)

        # 4. Final check
        mass_rate = dM_dt(C_post, p)

        if mass_rate = -A:
            results.append(tau_c_critical)
        else:
            results.append(-1.0)

    # Format and print the final results
    formatted_results = [f"{r:.7f}" if r != -1.0 else "-1.0" for r in results]
    print(f"[{','.join(formatted_results)}]")

solve()
