import numpy as np

def solve():
    """
    Formalizes and implements a validator for FHIR Observation resources for vital signs.
    It calculates a semantic interoperability degradation score based on a set of 
    pre-defined constraints related to FHIR, LOINC, and UCUM standards.
    """

    # --- Constraint Definitions ---
    CONSTRAINTS = {
        "urls": {
            "loinc": "http://loinc.org",
            "ucum": "http://unitsofmeasure.org",
            "category": "http://terminology.hl7.org/CodeSystem/observation-category",
        },
        "category_code": "vital-signs",
        "allowed_status": {'final', 'amended', 'corrected', 'preliminary'},
        "vitals": {
            "8867-4": {  # Heart rate
                "type": "scalar",
                "units": {'/min', '1/min', '{beats}/min'},
            },
            "8310-5": {  # Body temperature
                "type": "scalar",
                "units": {'Cel', 'K', '[degF]'},
            },
            "9279-1": {  # Respiratory rate
                "type": "scalar",
                "units": {'/min', '1/min'},
            },
            "85354-9": {  # Blood pressure panel
                "type": "panel",
                "components": {
                    "8480-6": {  # Systolic
                        "units": {'mm[Hg]', 'mmHg'}
                    },
                    "8462-4": {  # Diastolic
                        "units": {'mm[Hg]', 'mmHg'}
                    },
                },
            },
        },
    }

    # --- Test Suite Data ---
    test_cases = [
        # Case 1: Happy path, heart rate
        {
            "main_loinc": "8867-4",
            "resource": {
                "status": "final",
                "category": [{"system": "http://terminology.hl7.org/CodeSystem/observation-category", "code": "vital-signs"}],
                "code": {"system": "http://loinc.org", "code": "8867-4"},
                "subject": "present",
                "effectiveDateTime": "present",
                "valueQuantity": {"value": 72, "system": "http://unitsofmeasure.org", "code": "/min"}
            }
        },
        # Case 2: Body temperature with Kelvin
        {
            "main_loinc": "8310-5",
            "resource": {
                "status": "final",
                "category": [{"system": "http://terminology.hl7.org/CodeSystem/observation-category", "code": "vital-signs"}],
                "code": {"system": "http://loinc.org", "code": "8310-5"},
                "subject": "present",
                "effectiveDateTime": "present",
                "valueQuantity": {"value": 310.15, "system": "http://unitsofmeasure.org", "code": "K"}
            }
        },
        # Case 3: Heart rate with incorrect code system and unit
        {
            "main_loinc": "8867-4", # Assumed type for evaluation
            "resource": {
                "status": "final",
                "category": [{"system": "http://terminology.hl7.org/CodeSystem/observation-category", "code": "vital-signs"}],
                "code": {"system": "http://snomed.info/sct", "code": "364075005"},
                "subject": "present",
                "effectiveDateTime": "present",
                "valueQuantity": {"value": 70, "system": "http://unitsofmeasure.org", "code": "bpm"}
            }
        },
        # Case 4: Correct blood pressure panel
        {
            "main_loinc": "85354-9",
            "resource": {
                "status": "final",
                "category": [{"system": "http://terminology.hl7.org/CodeSystem/observation-category", "code": "vital-signs"}],
                "code": {"system": "http://loinc.org", "code": "85354-9"},
                "subject": "present",
                "effectiveDateTime": "present",
                "component": [
                    {"code": {"system": "http://loinc.org", "code": "8480-6"}, "valueQuantity": {"value": 120, "system": "http://unitsofmeasure.org", "code": "mm[Hg]"}},
                    {"code": {"system": "http://loinc.org", "code": "8462-4"}, "valueQuantity": {"value": 80, "system": "http://unitsofmeasure.org", "code": "mm[Hg]"}}
                ]
            }
        },
        # Case 5: Blood pressure panel missing diastolic and bad systolic unit
        {
            "main_loinc": "85354-9",
            "resource": {
                "status": "final",
                "category": [{"system": "http://terminology.hl7.org/CodeSystem/observation-category", "code": "vital-signs"}],
                "code": {"system": "http://loinc.org", "code": "85354-9"},
                "subject": "present",
                "effectiveDateTime": "present",
                "component": [
                    {"code": {"system": "http://loinc.org", "code": "8480-6"}, "valueQuantity": {"value": 16, "system": "http://unitsofmeasure.org", "code": "kPa"}}
                ]
            }
        },
        # Case 6: Respiratory rate missing category and wrong unit system
        {
            "main_loinc": "9279-1",
            "resource": {
                "status": "final",
                "category": None,
                "code": {"system": "http://loinc.org", "code": "9279-1"},
                "subject": "present",
                "effectiveDateTime": "present",
                "valueQuantity": {"value": 18, "system": "http://example.org/units", "code": "/min"}
            }
        },
        # Case 7: Heart rate with invalid status
        {
            "main_loinc": "8867-4",
            "resource": {
                "status": "entered-in-error",
                "category": [{"system": "http://terminology.hl7.org/CodeSystem/observation-category", "code": "vital-signs"}],
                "code": {"system": "http://loinc.org", "code": "8867-4"},
                "subject": "present",
                "effectiveDateTime": "present",
                "valueQuantity": {"value": 55, "system": "http://unitsofmeasure.org", "code": "/min"}
            }
        }
    ]

    def calculate_degradation(observation_data):
        loinc_code = observation_data["main_loinc"]
        obs = observation_data["resource"]
        vial_spec = CONSTRAINTS["vitals"].get(loinc_code)

        if not vial_spec:
            return 1.0 # Unknown vital sign type

        violations = 0

        # --- Scalar Observation Validation (Heart Rate, Temp, Resp Rate) ---
        if vial_spec["type"] == "scalar":
            total_weight = 9

            # 1. Status
            if obs.get("status") not in CONSTRAINTS["allowed_status"]:
                violations += 1
            # 2. Category
            category_present = obs.get("category") and isinstance(obs["category"], list)
            if not category_present or not any(
                c.get("system") == CONSTRAINTS["urls"]["category"] and c.get("code") == CONSTRAINTS["category_code"]
                for c in obs["category"]
            ):
                violations += 1
            # 3. Subject
            if not obs.get("subject"):
                violations += 1
            # 4. effectiveDateTime
            if not obs.get("effectiveDateTime"):
                violations += 1
            # 5. Code system
            if not obs.get("code") or obs["code"].get("system") != CONSTRAINTS["urls"]["loinc"]:
                violations += 1
            # 6. Code code
            if not obs.get("code") or obs["code"].get("code") != loinc_code:
                violations += 1
            # 7. valueQuantity presence and numeric value
            vq = obs.get("valueQuantity")
            if not vq or not isinstance(vq.get("value"), (int, float)):
                violations += 1
            # 8. valueQuantity system
            if not vq or vq.get("system") != CONSTRAINTS["urls"]["ucum"]:
                violations += 1
            # 9. valueQuantity code
            if not vq or vq.get("code") not in vial_spec["units"]:
                violations += 1

            return violations / total_weight

        # --- Panel Observation Validation (Blood Pressure) ---
        elif vial_spec["type"] == "panel":
            total_weight = 14

            # Parent-level checks (6 constraints)
            if obs.get("status") not in CONSTRAINTS["allowed_status"]: violations += 1
            category_present = obs.get("category") and isinstance(obs["category"], list)
            if not category_present or not any(c.get("system") == CONSTRAINTS["urls"]["category"] and c.get("code") == CONSTRAINTS["category_code"] for c in obs["category"]): violations += 1
            if not obs.get("subject"): violations += 1
            if not obs.get("effectiveDateTime"): violations += 1
            if not obs.get("code") or obs["code"].get("system") != CONSTRAINTS["urls"]["loinc"]: violations += 1
            if not obs.get("code") or obs["code"].get("code") != loinc_code: violations += 1

            # Component-level checks (4 constraints per component)
            components = {c["code"]["code"]: c for c in obs.get("component", []) if c.get("code")}

            for comp_loinc, comp_spec in vial_spec["components"].items():
                component = components.get(comp_loinc)
                if not component:
                    violations += 4  # All 4 checks fail if component is missing
                    continue

                comp_vq = component.get("valueQuantity")
                # 1. Numeric value
                if not comp_vq or not isinstance(comp_vq.get("value"), (int, float)): violations += 1
                # 2. System URL
                if not comp_vq or comp_vq.get("system") != CONSTRAINTS["urls"]["ucum"]: violations += 1
                # 3. Unit code
                if not comp_vq or comp_vq.get("code") not in comp_spec["units"]: violations += 1
                # 4. This is just a placeholder for the existence check, already passed.
                # The rule is 4 checks per component: exists, value, system, code.
                # If it doesn't exist, 4 violations. If it exists, up to 3 violations from value/system/code.
                # The implicit "existence" check passed, so no violation here. But we must count 4 constraints.

            return violations / total_weight

        return 1.0

    results = []
    for case in test_cases:
        score = calculate_degradation(case)
        results.append(f"{score:.3f}")

    # Final print statement in the exact required format.
    print(f"[{','.join(results)}]")

solve()
