import numpy as np
from math import pi, sqrt, exp
from scipy.integrate import quad

# Physical/mathematical primitives

def norm_sto_1s(zeta: float) -> float:
    # N_S(zeta) = (zeta^3 / pi)^(1/2)
    return (zeta**3 / pi) ** 0.5

def norm_gto_s(alpha: float) -> float:
    # N_G(alpha) = (2*alpha / pi)^(3/4)
    return (2.0 * alpha / pi) ** 0.75

def psi_sto_1s(r: float, zeta: float) -> float:
    return norm_sto_1s(zeta) * np.exp(-zeta * r)

def psi_sto_ng(r: float, zeta: float, n: int) -> float:
    # STO-nG parameters at zeta=1 (standard tabulated for 1s)
    sto_ng_params = {
        1: {
            "alpha": np.array([0.270950], dtype=float),
            "c":     np.array([1.000000], dtype=float),
        },
        2: {
            "alpha": np.array([0.151623, 0.851819], dtype=float),
            "c":     np.array([0.678914, 0.430129], dtype=float),
        },
        3: {
            "alpha": np.array([0.109818, 0.405771, 2.227660], dtype=float),
            "c":     np.array([0.444635, 0.535328, 0.154329], dtype=float),
        },
    }
    if n not in sto_ng_params:
        raise ValueError("Unsupported n for STO-nG. Allowed: 1, 2, 3.")
    alpha1 = sto_ng_params[n]["alpha"]
    c = sto_ng_params[n]["c"]
    # Scale exponents by zeta^2
    alpha = (zeta**2) * alpha1
    # Build contracted wavefunction value
    # psi = sum_i c_i * N_G(alpha_i(zeta)) * exp(-alpha_i(zeta) * r^2)
    Ng = (2.0 * alpha / pi) ** 0.75
    return float(np.dot(c, Ng * np.exp(-alpha * r * r)))

def radial_density(psi_val: float, r: float) -> float:
    # P(r) = 4*pi*r^2 * |psi|^2
    return 4.0 * pi * r * r * (psi_val * psi_val)

def mse_radial_density(zeta: float, rmax: float, n: int) -> float:
    # Define integrand of squared error in radial density
    def integrand(r):
        # Evaluate radial densities
        psi_sto = psi_sto_1s(r, zeta)
        psi_ng = psi_sto_ng(r, zeta, n)
        p_sto = radial_density(psi_sto, r)
        p_ng = radial_density(psi_ng, r)
        diff = p_ng - p_sto
        return diff * diff
    # Integrate from 0 to rmax
    integral, err = quad(integrand, 0.0, rmax, epsabs=1e-12, epsrel=1e-12, limit=200)
    return integral / rmax  # units: bohr^-2

def solve():
    # Define the test cases from the problem statement: (zeta, R_max, n)
    test_cases = [
        (1.0, 10.0, 1),  # Case 1
        (1.0, 10.0, 3),  # Case 2
        (1.0,  2.0, 3),  # Case 3
        (2.0, 10.0, 3),  # Case 4
        (0.5, 20.0, 3),  # Case 5
    ]

    results = []
    for zeta, rmax, n in test_cases:
        val = mse_radial_density(zeta, rmax, n)
        results.append(val)

    # Final print statement in the exact required format: scientific notation with 12 significant digits
    formatted = ",".join(f"{x:.12e}" for x in results)
    print(f"[{formatted}]")

if __name__ == "__main__":
    solve()
