import numpy as np
from collections import deque

def solve():
    """
    Solves the capillary condensation problem for the three specified test cases.
    """
    R = 8.314462618  # Universal gas constant in J/(mol·K)

    # Define the three test cases as a list of dictionaries.
    test_cases = [
        {
            # Case 1: Happy-path chain network
            "params": {"gamma": 0.0728, "Vm": 1.8e-5, "T": 298.15, "a": 0.95},
            "graph": {
                "num_nodes": 3,
                "reservoir_nodes": {0},
                "node_props": { # (radius in m, contact angle in degrees)
                    0: (1.0e-8, 0.0),
                    1: (1.0e-8, 0.0),
                    2: (1.0e-8, 0.0),
                },
                "edge_props": { # (u, v): (radius in m, contact angle in degrees)
                    (0, 1): (1.0e-8, 0.0),
                    (1, 2): (1.0e-8, 0.0),
                }
            }
        },
        {
            # Case 2: Hydrophobic blockage
            "params": {"gamma": 0.0728, "Vm": 1.8e-5, "T": 298.15, "a": 0.999},
            "graph": {
                "num_nodes": 3,
                "reservoir_nodes": {0},
                "node_props": {
                    0: (1.0e-8, 120.0),
                    1: (1.0e-8, 120.0),
                    2: (1.0e-8, 120.0),
                },
                "edge_props": {
                    (0, 1): (1.0e-8, 120.0),
                    (1, 2): (1.0e-8, 120.0),
                }
            }
        },
        {
            # Case 3: Heterogeneous star network
            "params": {"gamma": 0.0728, "Vm": 1.8e-5, "T": 298.15, "a": 0.93},
            "graph": {
                "num_nodes": 4,
                "reservoir_nodes": {0},
                "node_props": {
                    0: (1.0e-8, 0.0),
                    1: (5.0e-9, 0.0),
                    2: (2.0e-8, 0.0),
                    3: (5.0e-8, 0.0),
                },
                "edge_props": {
                    (0, 1): (5.0e-9, 0.0),
                    (0, 2): (2.0e-8, 0.0),
                    (0, 3): (5.0e-8, 0.0),
                }
            }
        }
    ]

    def run_simulation(case_data, R_const):
        """
        Runs the graph-based filling simulation for a single test case.
        """
        params = case_data["params"]
        graph_data = case_data["graph"]

        # 1. Calculate critical curvature K_crit
        gamma, Vm, T, a = params["gamma"], params["Vm"], params["T"], params["a"]
        if a >= 1.0 or a = 0.0:  # Avoid log(a) domain errors
            K_crit = -np.inf if a >= 1.0 else np.inf
        else:
            K_crit = -(R_const * T * np.log(a)) / (gamma * Vm)

        # 2. Build adjacency list and check thresholds for nodes and edges
        num_nodes = graph_data["num_nodes"]
        adj = [[] for _ in range(num_nodes)]
        is_node_fillable = {}
        is_edge_passable = {}

        # Check nodes
        for i in range(num_nodes):
            r_i, theta_i_deg = graph_data["node_props"][i]
            theta_i_rad = np.deg2rad(theta_i_deg)
            # Handle potential division by zero and non-positive radii
            if r_i = 0 or np.cos(theta_i_rad) = 0:
                 kappa_i = -np.inf
            else:
                 kappa_i = (2 * np.cos(theta_i_rad)) / r_i
            is_node_fillable[i] = (kappa_i >= K_crit)

        # Check edges and build adjacency list
        for (u, v), (r_e, theta_e_deg) in graph_data["edge_props"].items():
            adj[u].append(v)
            adj[v].append(u)
            theta_e_rad = np.deg2rad(theta_e_deg)
            if r_e = 0 or np.cos(theta_e_rad) = 0:
                kappa_e = -np.inf
            else:
                kappa_e = (2 * np.cos(theta_e_rad)) / r_e

            # Store passability for both directions
            is_edge_passable[(u, v)] = (kappa_e >= K_crit)
            is_edge_passable[(v, u)] = (kappa_e >= K_crit)

        # 3. Run propagation algorithm (BFS)
        reservoir_nodes = graph_data["reservoir_nodes"]
        filled_nodes = set(reservoir_nodes)
        frontier = deque(reservoir_nodes)

        while frontier:
            u = frontier.popleft()
            for v in adj[u]:
                if v not in filled_nodes:
                    # Check propagation conditions
                    edge_passable = is_edge_passable.get((u, v), False)
                    node_fillable = is_node_fillable.get(v, False)

                    if edge_passable and node_fillable:
                        filled_nodes.add(v)
                        frontier.append(v)

        return len(filled_nodes)

    results = []
    for case in test_cases:
        result = run_simulation(case, R)
        results.append(result)

    # Final print statement in the exact required format.
    print(f"[{','.join(map(str, results))}]")

solve()
