$$
\boxed{\bigvee_{n \in \omega} \left( \exists x_1 \dots \exists x_n \left( \left( \bigwedge_{1 \le i  j \le n} x_i \neq x_j \right) \land \left( \forall y \bigvee_{k=1}^n y = x_k \right) \right) \right)}
$$

$$
\boxed{
\left( (\forall x (x E x)) \land (\forall x \forall y (x E y \rightarrow y E x)) \land (\forall x \forall y \forall z ((x E y \land y E z) \rightarrow x E z)) \right) \land \left( \forall x \bigvee_{n=1}^{\infty} \exists y_1 \dots \exists y_n \left( \bigwedge_{1 \le i  j \le n} (y_i \ne y_j) \land \bigwedge_{i=1}^{n} (y_i E x) \land \forall z \left(z E x \rightarrow \bigvee_{i=1}^{n} (z = y_i) \right) \right) \right)
}
$$
